INSERT INTO post(title, content, author, created_at) SELECT 'Title' as TITLE, 'Lorem ipsum dolor sit amet, consectetur adipiscing elit. Nunc placerat tortor ex, vel fermentum orci mollis eget. Quisque ut hendrerit libero, ac fringilla tortor.' as CONTENT, CONCAT('anonymous', MOD(X, 9) + 1) as AUTHOR, (TIMESTAMP '2025-01-01 00:00:00' + (CURRENT_TIMESTAMP - TIMESTAMP '2025-01-01 00:00:00') * RAND()) AS CREATED_AT FROM (SELECT X FROM SYSTEM_RANGE(1, 50)) ORDER BY CREATED_AT ASC